  const [password, setPassword] = useState('')
  const [password2, setPassword2] = useState('')

  const userInput = (e) => {
    console.log(e);
  }

  return (
    <div className='auth-container'>
      <h1 className='title'>Sign Up</h1>
      <form action="" className="register-form">
        <label htmlFor="">User Name</label>
        <input className='register-inp' type="text"  onChange={userInput}/>

        <label htmlFor="">Email</label>
        <input className='register-inp' type="email" onChange={userInput}/>

        <label htmlFor="">Password</label>
        <input className='register-inp' type="password" />
        <label htmlFor="">Password(again)</label>
        <input className='register-inp' type="password" />

        <button type='submit' className='btn btn-outline-light sub-btn'>Register</button>
      </form>
    </div>
  );
}

export default Register
