    desktop: {
      breakpoint: { max: 1200, min: 880 },
      items: 5,
    },
    tablet: {
      breakpoint: { max: 880, min: 481 },
      items: 3,
    },
    mobile: {
      breakpoint: { max: 481, min: 0 },
      items: 2,
    },
  };

  return (
    <Carousel responsive={responsive} infinite={true} className="prod-row" centerMode={true} draggable={false}>
