import React, { useEffect, useState } from 'react'
import './Home.css'
import axios from '../../Constants/axios'
import SampleCard from '../../Components/SampleCard/SampleCard'

function Home() {

  const [imageArr, setImageArr] = useState([])
  const [currentPair, setCurrentPair] = useState(0)

  const swapPair = () => {
    if(currentPair > imageArr.length){
      setCurrentPair(0)
    }
    else{
      setCurrentPair(currentPair+1)
    }
  }

  useEffect(() => {
    axios.get("shop/sample-images").then((response) => {
      const data = response.data
      setImageArr(data);
      console.log(data)
    })
  },[])
  return (
    <div id="home">
      <div className="bg-image">
        <div className="blur-area"></div>
        <h1 className="intro">Welcome To Paradiso</h1>
        <h4 className="intro">We Have the Best Deals for You...</h4>
        <div className="sample-products">
          {
            imageArr.map(images => {
              return(
                images.map((item) => {
                  return(
                    <SampleCard key={item.id} image={item.image} />
                  )
                })
              )
            })
          }
        </div>
      </div>
    </div>
  );
}

export default Home
