import './App.css';
import Home from './Pages/Home/Home';
import Shop from './Pages/Shop/Shop';
import UserAuth from './Pages/UserAuth/UserAuth'
import NavBar from "./Components/Navbar/NavBar";
import Footer from "./Components/Footer/Footer";
import ProductView from './Pages/ProductView/ProductView';

import {BrowserRouter as Router, Routes, Route} from 'react-router-dom'
import axios from './Constants/axios'
import { useContext, useEffect } from 'react';

import CheckUser from './Store/Context';

function App() {

  const {setAuthentication} = useContext(CheckUser)

  useEffect(() => {
    axios.get('auth/check-user/').then((response) => {
      console.log(response.data);
      setAuthentication(response.data)
    })
  })
  return (
    <div className="App">
      <CheckUser>
      <NavBar />
      <Router>
        <Routes>
          <Route exact path="/" Component={Home} />
          <Route path="/auth/register" Component={UserAuth} />
          <Route path="/shop" Component={Shop} />
          <Route path="/shop/product/:id" Component={ProductView} />
        </Routes>
      </Router>
      <Footer/>
      </CheckUser>
    </div>
  );
}

export default App;
