import React, { useRef, useState, useEffect } from "react";
import "./Register.css";
import axios from "../../Constants/axios";
import { Navigate } from "react-router-dom";

function Register() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  const [isDisabled, setIsDisabled] = useState(true);

  const passwordRef = useRef();

  const nameInput = (e) => {
    setUsername(e.target.value);
  };
  const emailInput = (e) => {
    setEmail(e.target.value);
  };
  const passwordInput = (e) => {
    setPassword(e.target.value);
  };
  const passwordInput2 = (e) => {
    setPassword2(e.target.value);
  };

  useEffect(() => {
    if (password2 !== "" && password2 !== password) {
      passwordRef.current.classList.add("wrong-password");
      setIsDisabled(true);
    } else {
      passwordRef.current.classList.remove("wrong-password");
    }
    return () => {
      setIsDisabled(false);
    };
  }, [password, password2]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const userData = {
      username: username,
      email: email,
      password: password,
    };
    axios.defaults.xsrfCookieName = "csrftoken";
    axios.defaults.xsrfHeaderName = "X-CSRFToken";
    axios
      .post("auth/register/", userData)
      .then((response) => {
        console.log(response.data);
        <Navigate to='/'/>
      });
  };

  return (
    <div className="auth-container">
      <h1 className="title">Sign Up</h1>
      <form action="" className="register-form" onSubmit={handleSubmit}>
        <label htmlFor="">User Name</label>
        <input className="register-inp" type="text" onChange={nameInput} />

        <label htmlFor="">Email</label>
        <input className="register-inp" type="email" onChange={emailInput} />

        <label htmlFor="">Password</label>
        <input
          className="register-inp"
          type="password"
          onChange={passwordInput}
        />
        <label htmlFor="">Password(again)</label>
        <input
          ref={passwordRef}
          className="register-inp"
          type="password"
          onChange={passwordInput2}
        />

        <button
          type="submit"
          className="btn btn-outline-light sub-btn"
          disabled={isDisabled}
        >
          Register
        </button>
      </form>
    </div>
  );
}

export default Register;
