import React, {useEffect, useState} from 'react'
import './Shop.css'
import axio from '../../Constants/axios'
import ProductRow from '../../Components/ProductRow/ProductRow'

function Shop() {
    // const [productRow, setProductRow] = useState([])
    // const [category, setCategory] = useState('')
    const [data, setData] = useState(null)

    useEffect (() => {
        axio.get('shop/products').then((response) => {
            const data = response.data
            const keys = Object.entries(data)
            setData(keys)

            // const transformedArray = keys.map(([category, productRow]) => {
            //   setCategory(category)
            //   setProductRow(productRow)
            //   // console.log(category);
            //   // console.log(productRow);
            // });
        })
    }, [])
  return (
    <>
      {data && data.map(([category, productRow], index) => {
        // console.log(productRow[0]);
        return(
        <div key={index}>
        <h1 className='category-title'>{category}</h1>
        <ProductRow productRow={productRow[0]} category={category} id={index}/>
        </div>
        )
      })
        }
    </>
    
  );
}

export default Shop
