import React from 'react'
import './ProductCard.css'
import { BASE_URL } from '../../Constants/config';
import { NavLink } from 'react-router-dom';

function ProductCard(props) {
    const {product} = props

     const rupeeFormatter = new Intl.NumberFormat("en-IN", {
       style: "currency",
       currency: "INR",
     });

  return (
    <>
      <div className="card">
        <img
          src={BASE_URL + product.productimage_set[0].image}
          className="card-img-top"
          alt="..."
        />
        <div className="card-body">
          <h5 className="card-title prod-name">{product.name}</h5>
          <p className="card-text prod-price">
            {rupeeFormatter.format(product.price)}
          </p>
          <button className="btn btn-primary">
            <NavLink to={'/product/'+product.id}>Button</NavLink>
          </button>
        </div>
      </div>
    </>
  );
}

export default ProductCard
