import React, { useEffect, useState } from 'react'
import './ProductView.css'
import { useParams } from 'react-router-dom'
import axios from '../../Constants/axios'

import { BASE_URL } from '../../Constants/config'


function ProductView() {
    const param = useParams()
    const [product, setProduct] = useState(null)
    const [images, setImages] = useState(null)

    const rupeeFormatter = new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
    });

    useEffect(() => {
        axios.get(`shop/product/${param.id}`).then((response)=>{
         const data = response.data
            setProduct(data)
            setImages(data.productimage_set)
            return  data
        }).then((res)=>console.log(res.productimage_set))
    },[])
  return (
    <div className="container">
      {product && (
        <>
          <div className="image-section col-md-6">
            {images.map((image) => {
              return <img key={image.id} src={BASE_URL + image.image} alt="" />;
            })}
          </div>
          <div className="detailes col-md-6">
            <h1 className='prod-title m-3'>{product.name}</h1>
            <h5>{rupeeFormatter.format(product.price)}</h5>
          </div>
        </>
      )}
    </div>
  );
}

export default ProductView
