import React, { useEffect, useState } from "react";
import "./Home.css";
import axios from "../../Constants/axios";
import SampleCard from "../../Components/SampleCard/SampleCard";

function Home() {
  const [imageArr, setImageArr] = useState([]);
  const [currentPair, setCurrentPair] = useState(0);

  const swapPair = () => {
    console.log(currentPair);
    if (currentPair >= imageArr.length) {
      setCurrentPair(0);
      console.log('dec');
      console.log(imageArr.length);
    } else {
      setCurrentPair((prevPair) => prevPair+1);
      console.log('inc');
    }
    console.log(currentPair);
    console.log('len', imageArr.length);
  };
  useEffect(() => {
    axios.get("shop/sample-images").then((response) => {
      const data = response.data;
      setImageArr(data);

    })
  }, []);
useEffect(() => {
  const intervalId = setInterval(swapPair, 10000);

  return () => {
    clearInterval(intervalId); // Clear the interval when the component unmounts.
  };
}, []);



  return (
    <div id="home">
      <div className="bg-image">
        <div className="blur-area"></div>
        <h1 className="intro">Welcome To Paradiso</h1>
        <h4 className="intro">We Have the Best Deals for You...</h4>
        <div className="sample-products">
          {/* {imageArr.map((images) => {
            return images.map((item) => {
              return <SampleCard key={item.id} image={item.image} />;
            });
          })} */}
          {/* { imageArr && 
            imageArr[currentPair].map(item =>{
              return <SampleCard key={item.id} image={item.image} />;
            })
          } */}
        </div>
      </div>
    </div>
  );
}

export default Home;
