import React, {useRef, useState, useEffect} from 'react'
import './Register.css'
import axios from "../../Constants/axios";

function Register() {

  const [username, setUsername] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [password2, setPassword2] = useState('')

  const [submitButton, setSubmitButton] = useState(false)

  const passwordRef = useRef()

  const nameInput = (e) => {
    setUsername(e.target.value)
    
  }
  const emailInput = (e) => {
    setEmail(e.target.value);
  }
  const passwordInput = (e) => {
    setPassword(e.target.value);
  }
  const passwordInput2 = (e) => {
    setPassword2(e.target.value);
  };

    useEffect(() => {
      if (password2 !== "" && password2 !== password) {
        passwordRef.current.classList.add("wrong-password");
      } else {
        passwordRef.current.classList.remove("wrong-password");
      }
    }, [password, password2]);


    const handleSubmit = (e) => {
      e.preventDefault();
      const userData = {
        userName: username,
        email: email,

      }
      axios.post()
    }

  return (
    <div className="auth-container">
      <h1 className="title">Sign Up</h1>
      <form action="" className="register-form" onSubmit={handleSubmit}>
        <label htmlFor="">User Name</label>
        <input className="register-inp" type="text" onChange={nameInput} />

        <label htmlFor="">Email</label>
        <input className="register-inp" type="email" onChange={emailInput} />

        <label htmlFor="">Password</label>
        <input
          className="register-inp"
          type="password"
          onChange={passwordInput}
        />
        <label htmlFor="">Password(again)</label>
        <input
        ref={passwordRef}
          className="register-inp"
          type="password"
          onChange={passwordInput2}
        />

        <button type="submit" className="btn btn-outline-light sub-btn"
        disabled={submitButton}
        >
          Register
        </button>
      </form>
    </div>
  );
}

export default Register
